import {
  REMOVE_MOVIE,
  ADD_MOVIE,
  FETCH_DATA,
} from "./types";

const action = type => payload => ({
  type,
  payload
});

export const removeMovie = action(REMOVE_MOVIE);
export const addMovie = action(ADD_MOVIE);
export const fetchData = action(FETCH_DATA);

export function getData() {
  console.log('hello');
};
