import React from "react";
import List from "./components/List";
import * as actions from "./actions";
import { connect } from "react-redux";
import logo from "./logo.png";
import "./styles.css";

class App extends React.Component {
  componentDidMount() {
    const { myList, recommendations } = this.props;
    const data = fetch('https://5d11c0f984e9060014576512.mockapi.io/NetflixData')
      .then(response => response.json())
    myList = data.mylist;
    recommendations = data.recommendations
  }

  handleAddItem = payload => {
    this.props.addMovie(payload);
  };

  handleRemoveItem = payload => {
    this.props.removeMovie(payload);
  };

  render() {
    const { myList, recommendations } = this.props;
    return (
      <div className="App">
        <div className="LogoContainer">
          <img className="Logo" src={logo} alt="logo" />
        </div>

        <List
          title="My List"
          movie={myList}
          buttonName="remove"
          handleMethod={this.handleRemoveItem}
        />
        <List
          title="Recommendations"
          movie={recommendations}
          buttonName="add"
          handleMethod={this.handleAddItem}
        />
      </div>
    );
  }
}

const mapStateToProps = state => ({
  myList: state.myList,
  recommendations: state.recommendations
});

const mapDispatchToProps = dispatch => ({
  addMovie: payload => {
    dispatch(actions.addMovie(payload));
  },
  removeMovie: payload => {
    dispatch(actions.removeMovie(payload));
  }
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
