import {
  REMOVE_MOVIE,
  ADD_MOVIE,
  INITIAL_DATA_REQUEST,
  INITIAL_DATA_SUCCESS,
  INITIAL_DATA_FAILURE
} from "../types";

const initialState = {
  myList: [],
  recommendations: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case INITIAL_DATA_REQUEST:
    case INITIAL_DATA_SUCCESS:
      return {
        mylist: action.data[0].mylist,
        recommendations: action.data[0].recommendations
      };
    case INITIAL_DATA_FAILURE:
    case REMOVE_MOVIE:
      return {
        myList: state.myList.filter(item => item.id !== action.payload),
        recommendations: [
          ...state.recommendations,
          state.myList.filter(item => item.id === action.payload)[0]
        ]
      };
    case ADD_MOVIE:
      return {
        myList: [
          ...state.myList,
          state.recommendations.filter(item => item.id === action.payload)[0]
        ],
        recommendations: state.recommendations.filter(
          item => item.id !== action.payload
        )
      };
    default:
      return state;
  }
};

export default reducer;
